<?php/** * The interface to get course info list to syllabus *  * @author Qiang Runwei <qiangrw@gmail.com> * @todo When A course has many teachers , need to change code * @copyright LocalsNake Net League 2011 * @package interface * @subpackage course */      session_start();   require_once('sns_fns.php');   $user_id=$_SESSION['user_id'];   $conn=db_connect();   $course_info=array();   $array_courseinfo = get_jw_course_list($user_id,$conn);   for($i=0;$i<count($array_courseinfo);$i++) {       $element=$array_courseinfo[$i];	   $course_info[$i]['course_name'] = $element->course_name;	   $course_info[$i]['course_id']   = $element->course_id;	   $course_info[$i]['course_place']= $element->course_place;	   $course_info[$i]['course_time'] = $element->course_time;	   $course_info[$i]['course_type'] = $element->course_type;	   $course_info[$i]['course_teacher'] = $element->course_teacher;   }   echo json_encode($course_info);?>